document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll('input[data-submit-once="1"]').forEach((t=>{t.addEventListener("click",(t=>{const e=t.currentTarget,a=e.closest("form");if(null!==a&&a.checkValidity())return e.dataset.submited&&"true"===e.dataset.submited?(t.preventDefault(),t.stopPropagation(),!1):void(e.dataset.submited="true")}))}))}));
