document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll('input[data-submit-once="1"]').forEach((e=>{e.addEventListener("click",(e=>{const t=e.currentTarget,a=t.closest("form");if(null!==a&&a.checkValidity())return t.dataset.disabled&&"1"===t.dataset.disabled?(e.preventDefault(),e.stopPropagation(),!1):void(t.dataset.disabled="1")}))}))}));
